angular.module('zetta').directive('zettaWampumBelt', ['$compile', 'zettaShared', 
  function($compile, zettaShared) {

  $('#wampum').on('dblclick', function() { $('#wampum').hide(); });

  function textToColor(text, min, max) {
    if (text === null || text === undefined) {
      return 0;
    }

    if (!min) {
      min = 0;
    }

    if (!max) {
      max = 360;
    }

    var tryFloat = parseFloat(text);
    if (typeof text !== 'number' && isNaN(tryFloat)) {
      var code = text.toString().split('').map(function(c) {
        return c.charCodeAt(0);
      }).reduce(function(previous, current) {
        return previous + current;
      }, 0);

      return (code % max) - min;
    } else {
      if (tryFloat === max) {
        return 359;
      }

      if (val > 360) {
        val = val % 360;
      }

      var val = (tryFloat - min)/ (max - min);
      return Math.floor(val * 360);
    }
  }

  function textToSaturation(text) {
    var code = text.split('').map(function(c) {
      return c.charCodeAt(0);
    }).reduce(function(previous, current) {
      return previous + current;
    }, 0);

    return ((code * Math.floor(text.length/3)) % 100) + '%';
  }

  var UNIT_SIZE = 3;

  function drawCanvas(context, colors, cb) {
    var unitWidth = context.canvas.width / 36;
    var transitionWidth = unitWidth;
    var x = context.canvas.width - unitWidth;
    var y = 0;
    var width = unitWidth;
    var height = UNIT_SIZE;//context.canvas.height;

    colors.forEach(function(row) {
      row.forEach(function(color) {
        var w = width;

        context.fillStyle = 'hsl(' + color.hue + ', ' + color.saturation + ', 50%)';
        context.fillRect(x, y, w, height);
        x = x - unitWidth;
      });

      y = y + height;
      x = context.canvas.width - unitWidth;

      $('.page_content header').css('padding-top', $('#wampum canvas').height());
    });

    if (cb) cb();
  }

  function link(scope, element, attrs) {
    var canvas = element.children()[0];
    canvas.height = 0;
    var context = canvas.getContext('2d');

    function getColor(stream) {
      return {
        hue: textToColor(stream.current),
        saturation: textToSaturation(stream.name),
        lightness: '50%'
      };
    };

    function getStreamColor(value, min, max) {
      var val = textToColor(value, min, max);
      return {
        hue: val,
        saturation: '100%',
        lightness: '50%'
      };
    }

    function getTransitionColor(transition) {
      return {
        hue: textToColor(transition),
        saturation: '50%',
        lightness: '80%'
      };
    }

    var streams = [];
    var addedHrefs = [];
    scope.$watchCollection('servers', function() {
      scope.servers.forEach(function(server, i) {
        scope.$watchCollection('servers[' + i + '].devices', function() {
          if (!scope.servers[i].devices) {
            return;
          }
          scope.servers[i].devices.forEach(function(device, j) {
            scope.$watchCollection('servers[' + i + '].devices[' + j + '].streams', function() {
              var s = scope.servers[i].devices[j].streams;
              s.forEach(function(stream, k) {
                if (addedHrefs.indexOf(stream.href) !== -1) {
                  return;
                }
                streams.push(stream);
                var s = [];

                // order streams for multiple servers
                scope.servers.forEach(function(server) {
                  if (!server.devices || !server.devices.length) {
                    return;
                  }

                  server.devices.forEach(function(device) {
                    if (!device.streams || !device.streams.length) {
                      return;
                    }

                    device.streams.forEach(function(streamA) {
                      streams.forEach(function(streamB) {
                        if (streamA.href === streamB.href) {
                          s.push(streamB);
                        }
                      });
                    });
                  });
                });

                streams = s;
                addedHrefs.push(stream.href);
                update();

                var oldMessage;
                if (stream.socket.onmessage) {
                  oldMessage = stream.socket.onmessage;
                }

                stream.socket.onmessage = function(d) {
                  if (oldMessage) {
                    oldMessage(d);
                  }

                  var stream = scope.servers[i].devices[j].streams[k];
                  var colorIndex = null;

                  streams.forEach(function(s, index) {
                    if (s.href === stream.href) {
                      colorIndex = index;
                    }
                  });

                  if (colorIndex === null) {
                    return;
                  }

                  var d = stream.data;
                  if (d.length === 0) {
                    return;
                  }
                  var arr = d[d.length - 1];

                  var last;
                  if (stream.type === 'categorical') {
                    last = getColor(stream);
                  } else {
                    last = getStreamColor(arr[1], stream.min, stream.max);
                  }

                  colors[colorIndex].unshift(last);
                };
              });
            });
          });
        });
      });
    });

    var colors = [];
    var unitSize = UNIT_SIZE;

    window.onresize = function() {
      canvas.width = window.innerWidth;//unitSize * 36;
      canvas.height = streams.length * unitSize;
      context.fillStyle = 'rgb(222, 222, 222)';
      context.fillRect(0, 0, canvas.width, canvas.height);
      drawCanvas(context, colors);
    };

    var update = function() {
      if (streams.length === 0) {
        return;
      }

      canvas.width = window.innerWidth;//unitSize * 36;
      canvas.height = streams.length * unitSize;
      context.fillStyle = 'rgb(222, 222, 222)';
      context.fillRect(0, 0, canvas.width, canvas.height);

      angular.forEach(streams, function(stream, i) {
        var last = getStreamColor(stream);
        colors[i] = colors[i] || [];
        colors[i].push(last);
      });
    };

    update();
    
    var interval = setInterval(function() {
      streams.forEach(function(stream, i) {
        var last;
        if (stream.type === 'categorical') {
          last = getColor(stream);
        } else {
          last = getStreamColor(stream.current, stream.min, stream.max);
        }

        colors[i] = colors[i] || [];
        colors[i].unshift(last);
        colors[i] = colors[i].slice(0, 49);
      });

      drawCanvas(context, colors);
    }, 50);
  }

  return {
    restrict: 'E',
    scope: {
      servers: '='
    },
    template: '<canvas class="wampum" id="wampum" width="100%"></canvas>',
    link: link
  };
}])


angular.module('zetta').directive('zettaAction', ['$compile', 'navigator', function($compile, navigator) {
  function link(scope, element, attrs) {
    if (!scope.action) {
      return;
    }

    var container = $('<div>').addClass("actionContent " + scope.action.name);
    var visible = false;
    
    function Label(obj){
      var defaults = {
        'class' : "control-label pure-u-1-3",
        'for'   : "zettaAction",
        'text'  : "Action"
      }
      var params = $.extend(defaults, obj);
      return $('<label>')
        .addClass(params.class)
        .attr('for',params.for)
        .text(params.text);
    }
    
    function Button(obj, scope){
      var defaults = {
        'dclass'  : "pure-button",
        'class'   : "",
        'type'    : "submit",
        'icons'   : '<i class="fa fa-refresh fa-spin"></i><i class="fa fa-check"></i>',
        'html'    : "Update"
      }
      var params = $.extend(defaults, obj);
      var btn = $('<button>')
        .addClass(params.class)
        .addClass(params.dclass)
        .attr('type',params.type)
        .html(params.html + "&nbsp;" + params.icons);
      
      btn.click(function(e) {
        btn.addClass('loading');
        scope.action.execute(function() {
          btn.removeClass('loading');
          btn.addClass('success');
          setTimeout(function() {
            btn.removeClass('success');
          }, 1000);
        });
      });
      
      return btn;
      
    }
    
    function Input(obj){
      var defaults = {
        'name'        : "action",
        'class'       : "pure-input-2-3",
        'id'          : "zettaAction",
        'type'        : "text",
        'ng-model'    : "",
        'value'       : ""
      }
      var params = $.extend(defaults, obj);
      var output = $('<input>')
        .addClass(params.class)
        .attr('name', params.name)
        .attr('id', params.id)
        .attr('type', params.type)
        .attr('ng-model', params['ng-model'])
        .val(params.value);
      
      if(params.plaeholder !== 'undefined'){
        output.attr('placeholder', params.placeholder)
      }
      
      if(params['file-model'] !== 'undefined'){
        output.attr('file-model', params['file-model'])
      }
      
      return output;
      
    }
    
    for(var i = 0; i < scope.action.fields.length; i++) {
      var field = scope.action.fields[i];

      var label = Label({
          'for' : scope.action.name + field.name,
          'text': field.title || field.name
        });
      
      var controls = $('<div>').addClass('controls pure-g');
      
      var iput = {
          'name'      :   field.name,
          'id'        :   scope.action.name + field.name,
          'type'      :   field.type || 'text',
          'ng-model'  :   'action.fields[' + i + '].value',
          'value'     :   field.value
      }
       
      if(iput.type === 'text'){
        controls.addClass('text');
        iput.placeholder = field.title || field.name; 
        iput.class = "pure-input-1";
      }
      if(iput.type === 'file'){ 
        controls.addClass('file');
        iput['file-model'] = 'action.fields[' + i + '].file'; 
      }
      if(iput.type === 'hidden'){ 
        controls.addClass('hidden'); 
      }
      if(iput.type === 'number'){
        controls.addClass('number');
      }
      
      if (iput.type === 'radio' || iput.type === 'checkbox') {
        iput.value.forEach(function(val) {
          var input = $('<input>')
            .attr('name', iput.name)
            .attr('id', scope.action.name + field.name + val.value)
            .attr('type', iput.type)
            .attr('ng-model', iput['ng-model'])
            .val(val.value);

          $compile(input)(scope);

          var con = $('<div>').addClass('pure-u-1');

          con.append(input);

          var label = $('<label>')
            .attr('for', scope.action.name + field.name + val.value)
            .text(val.title || val.value);

          con.append('   ');
          con.append(label);
          controls.append(con);
        });
      } else {
        var input = Input(iput);
        
        $compile(input)(scope);

        controls.append(input);
      }

      if (iput.type !== 'hidden') {
        visible = true;
        if (iput.type !== 'text') { controls.prepend(label) };
      }
      
      container.append(controls);
    }; //for actions loop


    if (!visible) {
      container.addClass("trigger");
      var btn = Button({
        'class' : 'pure-button',
        'html'  : scope.action.name 
      }, scope);
      container.addClass("trigger");
    } else {
      var btn = Button({
        'class' : 'pure-button',
        'html'  : scope.action.name 
      }, scope);
    }

    $compile(btn)(scope);
      
    container.append(btn);
    
    element.replaceWith(container);
  }

  return {
    restrict: 'E',
    scope: {
      action: '=value'
    },
    link: link
  };
}])


angular.module('zetta').factory('zettaShared', ['$http', '$state', 'navigator', function($http, $state, navigator) {
  var state = {
    servers: [],
    root: null,
    breadcrumbs: null,
    pinned: [],
    muted: [],
    savedStreams: [],
    onStreamUpdate: function() { /* default no op */ }
  };

  var getAssumedStreamType = function(stream) {
    return isNaN(parseInt(stream.current))
            ? 'categorical'
            : 'numerical';
  };

  var wireUpStreams = function(device, cb) {
    device.streams.forEach(function(stream) {
      var oldOnMessage = stream.socket.onmessage;
      stream.socket.onmessage = function(event) {
        if (oldOnMessage) {
          oldOnMessage(event);
        }

        //Add data to model w/ timestamp here
        var d = JSON.parse(event.data);

        var update = {
          target: d.topic.replace(/\//g, '_'),
          data: d.data
        }

        var color;
        stream.data.push([new Date(), update.data]);

        stream.current = update.data;

        stream.type = getAssumedStreamType(stream);

        if (stream.min === null) {
          stream.min = d.data;
        }

        if (stream.max === null) {
          stream.max = d.data;
        }

        if (d.data < stream.min) {
          stream.min = d.data;
        }

        if (d.data > stream.max) {
          stream.max = d.data;
        }

        if(stream.data.length > 40){
          stream.data.shift();
        }

        cb();
      }
    });
  };

  var buildDeviceFromData = function(deviceData) {
    if (typeof deviceData === 'string') {
      deviceData = JSON.parse(deviceData);
    }

    var device = {
      available: true,
      properties: deviceData.properties,
      body: deviceData
    };

    deviceData.links.forEach(function(link) {
      if (link.rel.indexOf('up') !== -1) {
        var serverName = link.title;
        state.servers.forEach(function(s) {
          if (s.name === serverName) {
            device.server = s;
          }
        });
      }
      if (link.rel.indexOf('self') !== -1) {
        device.href = link.href;
      }
    });


    var objectStreamLinks = deviceData.links.filter(function(link) {
      return link.rel.indexOf('http://rels.zettajs.io/object-stream') !== -1;
    });

    if (objectStreamLinks.length) {
      device.streams = [];
    }

    objectStreamLinks.forEach(function(objectStream) {
      if (state.savedStreams.hasOwnProperty(objectStream.href)) {
        var stream = state.savedStreams[objectStream.href];
        device.streams.push(stream);
        return;
      }

      if (objectStream.title === 'logs') {
        device.monitorHref = objectStream.href;
      } else {
        var stream = {
          id: device.server.name.replace(/\./g, '-') + 'device' + device.server.devices.length + 'stream' + state.savedStreams.length + objectStream.title,
          name: objectStream.title,
          href: objectStream.href,
          socket: new WebSocket(objectStream.href),
          device: device,
          data: [],
          pinned: false,
          available: true,
          open: true,
          pinOpen: false,
          muteOpen: false,
          muted: false,
          min: null,
          max: null,
          type: null,
          current: objectStream.rel.indexOf('monitor') !== -1
                    ? device.properties[objectStream.title] : null,
        };

        stream.socket.onclose = function() {
          var oldOnMessage = stream.socket.onmessage;
          stream.socket = new WebSocket(stream.href);
          stream.socket.onmessage = oldOnMessage;
        };

        stream.type = getAssumedStreamType(stream);

        state.savedStreams[stream.href] = stream;
        device.streams.push(stream);
      }
    });

    device.links = deviceData.links;

    if (deviceData.actions) {
      device.actions = deviceData.actions.map(function(action, i) {
        action.device = device;
        action.available = true;
        action.id = device.server.name.replace(/\./g, '-') + 'device' + device.server.devices.length + 'action' + i + action.name;
        action.open = true;
        action.pinOpen = false;
        action.muteOpen = false;
        return action;
      }).sort(function(a, b) {
        var identifierA = a.name;
        var identifierB = b.name;

        if (identifierA > identifierB) {
          return 1;
        } else if (identifierA < identifierB) {
          return -1; 
        } else {
          return 0;
        }
      });
    }

    return device;
  };

  function loadServers(rootUrl, cb) {
    $http.get(rootUrl).then(function(response) {
      var data = response.data;
      if (typeof data === 'string') {
        data = JSON.parse(data);
      }

      var serverLinks = data.links.filter(function(link) {
        return link.rel.indexOf('http://rels.zettajs.io/server') !== -1;
      });

      if (serverLinks.length) {
        var server = serverLinks[0];
        state.servers.push({
          name: server.title,
          type: 'server',
          href: server.href,
          available: $state.params.filter ? $state.params.filter === server.title : true
        });
      }

      var peerLinks = data.links.filter(function(link) {
        return link.rel.indexOf('http://rels.zettajs.io/peer') !== -1;
      });

      peerLinks.forEach(function(peer) {
        state.servers.push({
          name: peer.title,
          type: 'peer',
          href: peer.href,
          available: $state.params.filter ? $state.params.filter === peer.title : true
        });
      });

      crawl(cb);
    });
  };

  var crawl = function(cb) {
    var serverCount = 0;
    state.servers.forEach(function(server) {
      $http.get(server.href).then(function(response) {
        var data = response.data;
        if (typeof data === 'string') {
          data = JSON.parse(data);
        }

        data.links.forEach(function(link) {
          if (link.rel.indexOf('monitor') !== -1) {
            server.monitorHref = link.href;
            server.monitorSocket = new WebSocket(link.href);
          }
        });
        
        if (!data.entities || !data.entities.length) {
          serverCount++;

          if (serverCount === state.servers.length) {
            if (cb !== undefined && cb !== null) cb(state.servers);
          }

          return;
        }

        server.actions = data.actions.map(function(action) {
          if (!action.execute) {
            action.execute = function(cb) {
              serverExecute(action, cb);
            }
          }

          return action;
        });

        server.devices = [];

        var devices = data.entities.filter(function(entity) {
          return entity.class.indexOf('device') !== -1;
        });

        var foundCount = 0;
        var foundDevices = [];
        devices.forEach(function(device) {
          var selfLink;
          device.links.forEach(function(link) {
            if (link.rel.indexOf('self') !== -1) {
              selfLink = link;
            }
          });

          if (!selfLink) {
            return;
          }

          $http.get(selfLink.href).then(function(response) {
            var deviceData = response.data;

            var device = buildDeviceFromData(deviceData);

            if (device.actions && device.actions.length) {
              device.actions = device.actions.map(function(action) {
                if (!action.execute) {
                  action.execute = function(cb) {
                    execute(action, cb);
                  };
                }

                return action;
              });
            }

            foundDevices.push(device);
            foundDevices = foundDevices.sort(function(a, b) {
              var identifierA = a.properties.name || a.properties.type;
              var identifierB = b.properties.name || b.properties.type;

              if (identifierA > identifierB) {
                return 1;
              } else if (identifierA < identifierB) {
                return -1; 
              } else {
                return 0;
              }
            });

            foundCount++;

            if (foundCount === devices.length) {
              server.devices = foundDevices;
              server.devices.forEach(function(device) {
                wireUpStreams(device, function() {
                  state.onStreamUpdate();
                });
                
                if (device.streams) {
                  device.streams.forEach(function(stream) {
                    stream.open = true;
                  });
                }

                if (device.actions) {
                  device.actions.forEach(function(action) {
                    action.open = true;
                  });
                }
              });

              serverCount++;
              if (serverCount === state.servers.length) {
                if (cb !== undefined && cb !== null) cb(state.servers);
              }
            }
          });
        });
      });
    });
  };

  var serverExecute = function(action, cb) {
    navigator.execute(action).then(function(result) {
      cb(result);
    });
  };

  var execute = function(action, cb) {
    navigator.execute(action).then(function(result) {
      if (result.noop) {
        return;
      }
    
      var data = result.data;
      var device = state.buildDeviceFromData(data);

      if (device.server) {
        for (var i = 0; i < device.server.devices.length; i++) {
          var d = device.server.devices[i];
          if (d.href === device.href) {
            d.body = device.body;
            d.properties = device.properties;
            if (!device.actions && device.actions.length) {
              return;
            }

            var earlierActions = d.actions;

            var newActions = device.actions.map(function(action) {
              action.device = device;
              action.execute = function() {
                execute(action);
              };
              return action;
            });

            var earlierNames = earlierActions.map(function(action) {
              return action.name;
            });

            var newNames = newActions.map(function(action) {
              return action.name;
            });

            earlierActions.forEach(function(action) {
              var index = newNames.indexOf(action.name);
              if (index === -1) {
                action.open = false;
                action.available = false;
                newActions.push(action);
              }
            });

            var resolvedNames = newActions.map(function(action) {
              return action.name;
            });

            state.pinned.filter(function(characteristic, i) {
              if (!characteristic.type && characteristic.device.href == device.href) { // if it's an action
                var index = resolvedNames.indexOf(characteristic.name);
                if (index !== -1) {
                  newActions[index].pinned = true;
                  newActions[index].pinOpen = true;
                  state.pinned[i] = newActions[index];
                }
              }
            });

            state.muted.filter(function(characteristic, i) {
              if (!characteristic.type && characteristic.device.href == device.href) { // if it's an action
                var index = resolvedNames.indexOf(characteristic.name);
                if (index !== -1) {
                  newActions[index].muted = true;
                  newActions[index].muteOpen = true;
                  state.muted[i] = newActions[index];
                }
              }
            });

            device.actions = newActions.sort(function(a, b) {
              var identifierA = a.name;
              var identifierB = b.name;

              if (identifierA > identifierB) {
                return 1;
              } else if (identifierA < identifierB) {
                return -1; 
              } else {
                return 0;
              }
            });

            device.server.devices[i].actions = device.actions;
          }

          if (cb) cb();
        }
      }
    });
  };

  state.wireUpStreams = wireUpStreams;
  state.getAssumedStreamType = getAssumedStreamType;
  state.buildDeviceFromData = buildDeviceFromData;
  state.loadServers = loadServers;
  state.execute = execute;
  state.query = null;


  return { state: state };
}]);

angular.module('zetta')
.filter('prettify', function() {
  return function(obj) {
    return JSON.stringify(obj, function(key, val) {
      return (key === '$$hashKey') ? undefined : val;
    }, 2);
  };
})
.filter('pluralize', function() {
  return function(ordinal, noun) {
    if (ordinal == 1) {
      return ordinal + ' ' + noun;
    } else {
      var plural = noun;
      if (noun.substr(noun.length - 2) == 'us') {
        plural = plural.substr(0, plural.length - 2) + 'i';
      } else if (noun.substr(noun.length - 2) == 'ch' || noun.charAt(noun.length - 1) == 'x' || noun.charAt(noun.length - 1) == 's') {
        plural += 'es';
      } else if (noun.charAt(noun.length - 1) == 'y' && ['a','e','i','o','u'].indexOf(noun.charAt(noun.length - 2)) == -1) {
        plural = plural.substr(0, plural.length - 1) + 'ies';
      } else if (noun.substr(noun.length - 2) == 'is') {
        plural = plural.substr(0, plural.length - 2) + 'es';
      } else {
        plural += 's';
      }
      return ordinal + ' ' + plural;
    }
  };
})
.filter('datetime', function($filter) {
  return function(input){
    if(input == null){ return ""; } 
    var _date = $filter('date')(new Date(input), 'yy-dd-MMM HH:mm:ss.sss');
    return _date.toUpperCase();
  };

})
.filter('elapsed', function() {
 return function(input){ 
  if(input < 1000){return input + " ms";}
  else if(input < (1000 * 60)){ return (input / 1000).toFixed(1) + " s"; }
  else if(input < (1000 * 60 * 60)){
    return (input / (1000 * 60)).toFixed(2) + " m";
  }else if(input < (1000 * 60 * 60 * 24)){
    var h = parseInt(input / (1000 * 60 * 60));
    var min = (input / (1000 * 60)) % 60;
    return h + "h " + min + "m";
  }
 }
})
.filter('idSafe', function(){
  return function(input){ return input.replace(/([^A-z0-9])+/g,''); }
})
.filter('purify', function() {
  return function(input) {
    var purified = {};

    ['class', 'properties', 'entities', 'actions', 'links'].forEach(function(prop) {
      if (input.hasOwnProperty(prop)) {
        purified[prop] = input[prop];
      }
    });

    if (purified.actions) {
      purified.actions = purified.actions.map(function(action) {
        var a = {};
        ['class', 'name', 'method', 'href', 'fields'].forEach(function(prop) {
          if (action.hasOwnProperty(prop)) {
            a[prop] = action[prop];
          }
        });

        return a;
      });
    }

    return purified;
  };
});

angular
  .module('siren', ['ui.state'])
  .provider('classRouter', function() {
      var map = {};

      this.when = function(klass, state) {
        if (!klass) {
          map[null] = { known: false, state: state };
          return this;
        }
        var c = klass.sort().join(' ');
        map[c] = { known: true, state: state };
        return this;
      };
      
      this.otherwise = function(state) {
        map[null] = { known: false, state: state };
      };

      this.$get = function() {
        return {
          resolve: function(klass) {
            if (!klass) {
              return map[null];
            }
            var c = klass.sort().join(' ');
            return map[c] || map[null];
          }
        };
      };
  })
  .factory('navigator', ['$http', '$rootScope', '$q', '$state', 'classRouter',
      function($http, $rootScope, $q, $state, classRouter) {
    return {
      cache: [],
      current: null,
      redirectOrFetch: function(url, params) {
        return this.fetch(url, params, true);
      },
      fetch: function(url, params, redirectIfKnown) {
        if (this.cache.length) {
          this.current = this.cache.pop();
          return $q.when(this.current.entity);
        }

        var immediateReturn = true;

        return this.transitionTo(url, params, immediateReturn, redirectIfKnown);
      },
      transitionTo: function(url, params, immediateReturn, redirectIfKnown) {
        if (typeof url === 'object' && url.href) {
          url = url.href;
        }

        var self = this;
        var deferred = $q.defer();

        $http.get(url).success(function(data, status, headers, config) {
          var state = classRouter.resolve(data.class);

          self.current = { state: state, entity: data };

          // only cache if the following state must fetch.
          if (!immediateReturn || (redirectIfKnown && state.known)) {
            self.cache.push(self.current);
          }
          
          $rootScope.$broadcast('entityChangeSuccess', data);

          $state.transitionTo(state.state, params);

          var stateIsUnknown = !state.known;
          var resolveIfKnown = !redirectIfKnown;
          
          if (immediateReturn && (stateIsUnknown || resolveIfKnown)) {
            deferred.resolve(data);
          }
        }).error(function(data, status, headers, config) {
          console.log('error');
          deferred.reject(status);
        });

        if (immediateReturn) {
          return deferred.promise;
        }
      },
      execute: function(action) {
        var contentType = action.type || 'application/x-www-form-urlencoded';
        var options = {
          method: action.method || 'GET',
          url: action.href,
          headers: {
            'Content-Type': contentType,
            'Accept': 'application/vnd.siren+json, application/json, text/plain, */*'
          }
        };

        if (options.method === 'GET') {
          var params = {};

          angular.forEach(action.fields, function(field) {
          console.log(field);
            params[field.name] = field.value;
          });

          var serialize = function(obj) {
            var str = [];
            for(var p in obj)
              if (obj.hasOwnProperty(p)) {
                str.push(encodeURIComponent(p) + "=" + encodeURIComponent(obj[p]));
              }
            return str.join("&");
          };

          options.url = options.url.split('?')[0] + '?' + serialize(params); 

          //$state.transitionTo('entity', { url: url });

          var deferred = $q.defer();

          //deferred.resolve({ noop: true });
          $http(options).success(function(data, status, headers, config) {
            deferred.resolve({ data: data, config: config });
          })
          .error(function(data, status, headers, config) {
            deferred.reject(status);
          });

          return deferred.promise;
        } else {
          if (contentType === 'application/json') {
            options.data = {};
            angular.forEach(action.fields, function(field) {
              options.data[field.name] = field.value;
            });
          } else if (contentType === 'application/x-www-form-urlencoded') {
            var data = [];
            angular.forEach(action.fields, function(field) {
              data.push(encodeURIComponent(field.name) + '=' + encodeURIComponent(field.value));
            });

            options.data = data.join('&');
          }

          var deferred = $q.defer();

          $http(options).success(function(data, status, headers, config) {
            deferred.resolve({ data: data, config: config });
          })
          .error(function(data, status, headers, config) {
            deferred.reject(status);
          });

          return deferred.promise;
        }
      }
    };
  }]);

angular.module('zetta').controller('DeviceCtrl', [
  '$scope', '$state', 'zettaShared', function($scope, $state, zettaShared) {
  $scope.device = null;
  $scope.request = null;

  $scope.init = function() {
    var rootUrl = zettaShared.state.root;

    if (!rootUrl) {
      var parser = document.createElement('a');
      parser.href = $state.params.url;
      rootUrl = parser.protocol + '//' + parser.hostname;
      if (parser.port) {
        rootUrl += ':' + parser.port;
      }

      zettaShared.state.root = rootUrl;
    }

    zettaShared.state.onStreamUpdate = function() {
      $scope.$apply();
    };

    var anchor = document.createElement('a');
    anchor.href = $state.params.url;

    $scope.request = ['GET ' + anchor.pathname,
      'Host: ' + anchor.hostname,
      'Accept: application/vnd.siren+json'].join('\r\n');

    if (!zettaShared.state.servers.length) {
      zettaShared.state.loadServers(rootUrl, function() {
        findDevice();
      });
    } else {
      findDevice();
    };

    $scope.stateLogs = [];
  };

  var findDevice = function() {
    zettaShared.state.servers.forEach(function(server) {
      if (!server.devices) {
        return;
      }

      server.devices.forEach(function(device) {
        if (device.href === $state.params.url) {
          $scope.device = device;
          $scope.logger(device.monitorHref);

          zettaShared.state.breadcrumbs = [
            { title: 'root', href: zettaShared.state.root },
            { title: server.name, href: zettaShared.state.root, filter: server.name },
            { title: device.properties.name || device.properties.type }
          ];
        }
      });
    });
  };

  $scope.loggerSocket = null;
  $scope.loggerUrl = null;
  $scope.logger = function(url){
    if ($scope.loggerSocket && $scope.loggerUrl === url) {
      return;
    } else {
      if ($scope.loggerSocket) {
        $scope.loggerSocket.close();
      }

      $scope.loggerUrl = url;
    }

    $scope.loggerSocket = new WebSocket($scope.loggerUrl);
    
    $scope.loggerSocket.onmessage = function(event) {
      var d = JSON.parse(event.data);
      
      var dt = new Date(d.timestamp);
      var etime = 0;
      if($scope.stateLogs.length){
        etime = d.timestamp -  $scope.stateLogs[0].time.time;
      }
      //console.log(d);
      $scope.$apply(function() {
        $scope.stateLogs.unshift({
          transition: d.transition,
          state: d.properties.state,
          msg: (d.input[0]) ? d.input[0].value : null,
          time: {
            time: d.timestamp,
            localTime: dt.toLocaleString(),
            elapsed: etime
          }
        });

        if($scope.stateLogs.length > 10) {
          $scope.stateLogs.pop()
        }
      });
    }
  };
}]);

angular.module('zetta').controller('HeaderCtrl', [
  '$scope', '$state', '$http', 'navigator', 'zettaShared', function($scope, $state, $http, navigator, zettaShared) {

    $scope.shared = zettaShared.state;
    $scope.servers = $scope.shared.servers;
    $scope.breadcrumbs = $scope.shared.breadcrumbs;
    $scope.follow = function(url, filter) {
      url = url || $scope.shared.root;
      var params = { url: url };

      if (filter) {
        $state.params.url = $scope.shared.root;
        params.filter = filter;
      }

      navigator.transitionTo(url, params);
    }

    $scope.$watchCollection('shared', function() { 
      $scope.breadcrumbs = $scope.shared.breadcrumbs;
    });
}]);

angular.module('zetta').controller('MainCtrl', [
  '$scope', '$state', 'navigator', 'appState', 'zettaShared',
  function($scope, $state, navigator, appState, zettaShared) {
    zettaShared.state.breadcrumbs = [];
    zettaShared.state.servers = [];
    zettaShared.state.pinned = [];
    zettaShared.state.muted = [];

    $scope.init = function() {
      $scope.params = { url: appState.url || '' };
    };

    $scope.errMsg = null;
    
    $scope.fetchUrl = function(params) {
      $scope.errMsg = null;
      if (!params.url) {
        return;
      }
      var url = params.url;
      appState.url = url;

      var index = $scope.serverUrls.indexOf(url);
      if (index > -1) {
        $scope.serverUrls.splice(index, 1);
      }

      $scope.serverUrls.unshift(url);

      localStorage.serverUrls = JSON.stringify($scope.serverUrls);

      var navigationPromise = navigator.transitionTo(url, { url: url }, true);
      navigationPromise.catch(function(status, url) {
        $scope.errMsg = 'Bad server retrieval. Status Code: ' + status;
        //console.log('Bad server retrieval. Status Code: ' + status + url); 
      });

    };

    if (!localStorage.serverUrls) {
      localStorage.serverUrls = JSON.stringify([]);
    }

    $scope.serverUrls = JSON.parse(localStorage.serverUrls);
  }
]);

angular.module('zetta').controller('OverviewCtrl', [
  '$scope', '$state', '$http', '$location', '$window', 'navigator', 'zettaShared',
    function($scope, $state, $http, $location, $window, navigator, zettaShared) {
  $scope.pinned = zettaShared.state.pinned;
  $scope.servers = zettaShared.state.servers;
  $scope.muted = zettaShared.state.muted;
  $scope.query = zettaShared.state.query;
  $scope.activeQuery = null; 
  $scope.queryError = null;
  
  $scope.pageNav = null;
  $scope.loading = true;
  $scope.hasDevices = false;

  $scope.init = function() {
    console.log('scope.query:', $scope.query);
    console.log('shared.query:', zettaShared.state.query);
    loadServers();
  };

  $scope.$watch('pageNav', function() {
    if ($scope.pageNav === null) {
      return;
    }

    if($('#' + $scope.pageNav).length){
      var pos = $('#' + $scope.pageNav)[0].offsetTop - $('nav:first').height() - $('#wampum').height() -32;
      
      $(window).scrollTop(pos);
      $scope.pageNav = null;
    }
  });    

  $scope.clearQuery = function() {
    $scope.servers.forEach(function(server) {
      server.lastSearch = null;
      server.devices.forEach(function(device) {
        device.available = true;
      });
    });

    $scope.activeQuery = null;
    $scope.query = null;
    zettaShared.state.query = null;
  };

  $scope.submitQuery = function() {
    var isValid = false;

    try {
      caql.parse($scope.query);
      $scope.queryError = null;
      $scope.activeQuery = $scope.query;
      isValid = true;
      zettaShared.state.query = $scope.query;
    } catch(e) {
      $scope.queryError = e.message;
      $scope.activeQuery = null;
    }

    if (!isValid || !$scope.servers.length) {
      return;
    }

    $scope.servers.forEach(function(server) {
      var queryActions = server.actions.filter(function(action) {
        return action.name === 'query-devices';
      });

      if (!queryActions.length) {
        return;
      }

      var queryAction = queryActions[0];
      console.log(queryAction);

      var qlFields = queryAction.fields.filter(function(field) {
        return field.name === 'ql';
      });

      if (!qlFields.length) {
        return;
      }

      var qlField = qlFields[0];

      qlField.value = $scope.query;

      server.devices.forEach(function(device) {
        device.available = false;
      });

      queryAction.execute(function(result) {
        server.lastSearch = result.config.url;
        var data = result.data;
        if (!data.entities.length) {
          return;
        }
        data.entities.forEach(function(entity) {
          var selfHref;

          entity.links.forEach(function(link) {
            if (link.rel.indexOf('self') !== -1) {
              selfHref = link.href;
            }
          });

          if (!selfHref) {
            return;
          }

          server.devices.forEach(function(device) {
            if (device.href === selfHref) {
              device.available = true;
            }
          });
        });
        console.log(data);
      });
    });
  };
      
  function loadServers() {
    
    zettaShared.state.root = $state.params.url;
    zettaShared.state.breadcrumbs = [];
    zettaShared.state.onStreamUpdate = function() {
      $scope.$apply();
    };

    var rootUrl = zettaShared.state.root;

    if (!rootUrl) {
      var parser = document.createElement('a');
      parser.href = $state.params.url;
      rootUrl = parser.protocol + '//' + parser.hostname;
      if (parser.port) {
        rootUrl += ':' + parser.port;
      }

      zettaShared.state.root = rootUrl;
    }

    if ($state.params.filter) {
      $scope.filter = $state.params.filter;

      zettaShared.state.breadcrumbs = [
        { title: 'root', href: $state.params.url },
        { title: $state.params.filter }
      ];
    }

    if (!zettaShared.state.servers.length) {
      zettaShared.state.loadServers(rootUrl, function() {
        if ($state.params.filter) {
          filterServer();
        } else {
          zettaShared.state.servers.forEach(function(server) {
            server.available = true;
            if (server.devices && !$scope.hasDevices) {
              $scope.hasDevices = true;
            }
          })
        }

        $scope.loading = false;
      });
    } else {
      if ($state.params.filter) {
        filterServer();
      } else {
        zettaShared.state.servers.forEach(function(server) {
          server.available = true;
          if (server.devices && !$scope.hasDevices) {
            $scope.hasDevices = true;
          }
        })
      }
      $scope.loading = false;
    }
  }

  $scope.loadServer = function(server) {
    $state.params.filter = server.name;
    $location.search($state.params);
    loadServers();
    $window.scrollTo(0, 0);
    
  };

  var filterServer = function() {
    var width = $('.dnastrip canvas').width();
    var height = $('.dnastrip canvas').height();

    $scope.servers.forEach(function(server) {
      if (server.name !== $state.params.filter) {
        server.available = false;
      } else {
        if (!server.devices) {
          return;
        }

        server.devices.forEach(function(device) {
          if (!device.streams) {
            return;
          }

          device.streams.forEach(function(stream) {
            if (typeof stream.refresh === 'function') {
              setTimeout(function() {
                stream.refresh(width);
              }, 100);
            }
          });
        });
      }
    });
  };

  $scope.resolve = function(href) {
    navigator.transitionTo(href, { url: href });
  };

  $scope.pin = function(characteristic) {
    characteristic.pinned = true;

    if (characteristic.muted) {
      $scope.unmute(characteristic);
    }

    if ($scope.pinned.indexOf(characteristic) === -1) {
      $scope.pinned.push(characteristic);
      setTimeout(function() {
        characteristic.pinOpen = true;
        $scope.$apply();
      }, 1);
    } else {
      $scope.unpin(characteristic);
    }
  };

  $scope.unpin = function(characteristic) {
    var index = $scope.pinned.indexOf(characteristic);
    if (index > -1) {
      characteristic.pinned = false;
      characteristic.pinOpen = false;
      setTimeout(function() {
        $scope.pinned.splice(index, 1);
      }, 1);
    }
  };

  $scope.mute = function(characteristic) {
    if (characteristic.pinned) {
      $scope.unpin(characteristic);
    }

    if ($scope.muted.indexOf(characteristic) === -1) {
      $scope.muted.push(characteristic);
      setTimeout(function() {
        characteristic.open = false;
        characteristic.muteOpen = true;
        $scope.$apply(function() {
          setTimeout(function() {
            characteristic.muted = true;
            $scope.$apply();
          }, 500);
        });
      }, 1);
    } else {
      $scope.unmute(characteristic);
    }
  };

  $scope.unmute = function(characteristic) {
    var index = $scope.muted.indexOf(characteristic);
    if (index > -1) {
      $scope.muted[index].muted = false;
      characteristic.muteOpen = false;
      setTimeout(function() {
        if (characteristic.available) {
          characteristic.open = true;
        }
        $scope.muted.splice(index, 1);
        $scope.$apply();
      }, 1);
    }
  };
}]);
